import {Link} from "react-router-dom"
import { React,useEffect,useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faTrashCan,faPenToSquare } from '@fortawesome/free-regular-svg-icons'

function Home() {

  const [users,setUsers] = useState([]);
  useEffect(()=>{
    const fetchAllData = async()=>{
        try {
          const res = await axios.get("http://localhost:5000/");
          setUsers(res.data);
        } catch (err) {
          console.log(err);
        }
    };
    fetchAllData();
  },[])

  return (
    <div className='container'>
        <h1>Home</h1>
        <table class=" table table-striped table-hover">
        
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Axe</th>
            <th scope="col">Nom Projet</th>
            <th scope="col">Date DÃ©but</th>
            <th scope="col">Date Fin</th>
            <th scope="col">Module</th>
            <th scope="col">Nom Etablissement</th>
            <th scope="col">Annee Scolaire</th>
            <th scope="col">Status</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
    
            {
              users.map(user => (
                <tr key={user.id}>
                  <th>{user.id}</th>
                  <td>{user.axe_id}</td>
                  <td>{user.projet_id}</td>
                  <td>{user.email}</td>
                  <td>{user.email}</td>
                  <td>{user.email}</td>
                  <td>{user.email}</td>
                  <td>{user.email}</td>
                  <td><span className="badge text-bg-primary ">{user.status}</span></td>
                  <td>
                  <Link to={`/participant/${user.id}`} className='btn btn-success btn-sm'>Ajouter un participant</Link>
                  <Link to={`/update/${user.id}`} className='btn btn-primary btn-sm mx-3'><FontAwesomeIcon icon={faPenToSquare} /></Link>
                  <Link to={`/delete/${user.id}`} className='btn btn-danger btn-sm'><FontAwesomeIcon icon={faTrashCan} /></Link>
                  </td>
                </tr>
              ))
            }    
    
        </tbody>
      </table>
    </div>
  )
}

export default Home
