import React from 'react'
import {Link} from "react-router-dom"
import { useEffect,useState } from 'react';

function Home() {

  useEffect(()=>{
    const fetchAllData = async()=>{
        try {
          const res = await axios.get("http://localhost:5000/");
          setUsers(res.data);
        } catch (err) {
          console.log(err);
        }
    };
    fetchAllData();
  },[])

  return (
    <div className='container'>
        <h1>Home</h1>
        <table class=" table table-striped table-hover">
        
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nom</th>
            <th scope="col">Prenom</th>
            <th scope="col">Email</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
    
            {
              users.map(user => (
                <tr key={user.id}>
                  <th>{user.id}</th>
                  <td>{user.nom}</td>
                  <td>{user.prenom}</td>
                  <td>{user.email}</td>
                  <td>
                  <Link to={`/user_details/${user.id}`} className='btn btn-success btn-sm'>Show</Link>
                  <Link to={`/update/${user.id}`} className='btn btn-primary btn-sm mx-3'>Update</Link>
                  <Link to={`/delete/${user.id}`} className='btn btn-danger btn-sm'>Delete</Link>
                  </td>
                </tr>
              ))
            }    
    
        </tbody>
      </table>
    </div>
  )
}

export default Home
