import Header from './Header'
import { Link,useNavigate,useParams } from 'react-router-dom'
import { React,useEffect, useState } from 'react'
import axios from 'axios'


function UpdateParticipant() {
    
    const {id1} = useParams();
    const {id2} = useParams();
    const [data,setData] = useState([])
    const [etab,setEtab] = useState([])
    const [values,setValues] = useState({
        ppr:"",
        nom_pr:"",
        etablissement_id:"",
        formation_id:id1
    })

    useEffect(()=>{
        const fetchData = async ()=>{
          try {
    
            const res = await axios.get("http://localhost:5000/participant/"+id2)
            // console.log(res.data)
            setData(res.data)
            
          } catch (error) {
            console.log(error);
          }


          try {
          const res = await axios.get("http://localhost:5000/etablissement/")
            // console.log(res.data)
            setEtab(res.data)
            
          } catch (error) {
            console.log(error);
          }
        }
        
        fetchData();
      },[])


    const handleChange = (e) =>{
        setValues((prev) =>({...prev,[e.target.name]: e.target.value}))
    }

    const handleSubmit = async (e) =>{
        e.preventDefault()
        // console.log(values);

        try {
            const res =  await axios.put("http://localhost:5000/update_participant/"+id2)
            navigate(-1)
        } catch (error) {
            console.log(err)
        }
    }

  return (
    <div className='container'>
         <Header />
        <h3>Modifier</h3>
        <hr />
        <form onSubmit={handleSubmit}  className='col-6'>
            <div className="mb-3">
                <label htmlFor="ppr" className='form-label'>PPR</label>
                {
                    data.map(item =>(
                        <input type="number" id='ppr' name='ppr' placeholder={item.ppr} min={1111111} max={9999999}  onChange={handleChange} className="form-control" required />
                    ))
                }
            </div>
            <div className="mb-3">
                <label htmlFor="nom_prof" className='form-label'>Nom Professeur </label>
                {
                    data.map(item=>(
                        <input type="text" id='nom_prof' name='nom_pr' placeholder={item.nom_pr} onChange={handleChange} className="form-control" required />
                    ))
                }
            </div>
            <div className="mb-3">
                <label htmlFor="nom_etab" className='form-label'>Nom Etablissement</label>
                <select id="nom_etab" name="etablissement_id" onChange={handleChange} className='form-control'>
                    {
                        data.map(item=>(
                            <option selected disabled value={item.etablissement_id}>{item.nom_etab}</option>
                        ))
                    }
                    {
                        etab.map(item=>(
                            <option value={item.etablissement_id}>{item.nom_etab}</option>
                        ))
                    }
                </select>
            </div>
                     
            <input type="submit" className='btn btn-success' value="Modifier" />
            <Link to={-1} className='btn btn-secondary mx-3'>Annuler</Link>
        </form>
    </div>
  )
}

export default UpdateParticipant
