import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Header from './Header'
import {Link,useNavigate} from "react-router-dom"


function Formation() {

  // Get Data From DB
  const [projetDB,setProjetDB] = useState([]);
  const [axeDB,setAxeDB] = useState([]);
  const [etabDB,setEtabDB] = useState([]);
  const [statusDB,setStatusDB] = useState([]);
  const navigate = useNavigate();

  // Values from Input to send
  const [values,setValues] = useState({
    axe:"",
    annee_scolaire:"",
    projet:"",
    module:"",
    status:"",
    lieuFormation:"",
    dateDebut:"",
    dateFin:"",
  })



  useEffect(()=>{

    // axios.get("http://localhost:5000/projet")
    // .then(res=>{
    //   setProjetDB(res.data);
    // })
    // .catch(err => console.log(err));

    axios.get("http://localhost:5000/axe")
    .then(res=>{
      setAxeDB(res.data);
    })
    .catch(err => console.log(err));

    axios.get("http://localhost:5000/etablissement")
    .then(res=>{
      setEtabDB(res.data);
    })
    .catch(err => console.log(err));

    axios.get("http://localhost:5000/status")
    .then(res=>{
      setStatusDB(res.data);
    })
    .catch(err => console.log(err));

  },[])

  const handleChange = (e) =>{

    // setValues((prev) =>({...prev,[e.target.name]: e.target.value}))
    
    
  }

  const handleSubmit = async (e) =>{
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/formation",values)
      navigate("/")
    } catch (error) {
      console.log(error)
    }
    // console.log(values)
  }

  // Get ID From Select Box Axe
  const handleCick = async(e) => {
    const selectedAxe = e.target.value
    const [isShow,setIsShow] = useState(false);


    try {
      const res = await axios.get("http://localhost:5000/projet/"+selectedAxe)
      
        // console.log(res.data)
        setProjetDB(res.data)
        setIsShow(value => !value);

        
    
    } catch (error) {
      console.log(error)
    }
    
  }

  return (
    <div className='container'>
      <Header />
      <form onSubmit={handleSubmit}>
        
        <div className="mb-3">
          <label htmlFor="Annee Scolaire" className="form-label">Annee Scolaire</label>
          <select name="annee_scolaire" id="Annee Scolaire" className="form-control"  onChange={handleChange} >
            <option selected>Select ...</option>
            <option value="2023/2024">2023/2024</option>
            <option value="2024/2025">2024/2025</option>
            <option value="2025/2026">2025/2026</option>
          </select>
        </div>
        <div className="mb-3">
          <div className="row">
            <div className="col">
            <label htmlFor="axe" className='form-label'>Axe</label>
              <select name="axe selectAxeID" id="axe" className="form-control"  onChange={(e) => values.nom = e.target.value} >
                 <option selected>Select ...</option>
            {
              axeDB.map((item)=>(
                <option value={item.axe_id} onClick={handleCick}>{item.nom_axe}</option>
              ))
            }
          </select>
          
            </div>
            
            <div className="col">
            <label htmlFor="projet"  className='form-label'>Projet</label>
          <select name="projet" id="projet" className="form-control"  onChange={handleChange}>
            <option selected>Select ...</option>
            {
              projetDB.map((item)=>(
                isShow = true ? <option value={item.projet_id}>{item.nom_projet}</option> : setIsShow(false) 
              ))
            }
          </select>
            </div>
            
          </div>
          
        </div>

        <div className="mb-3">
          <label htmlFor="module"  className='form-label'>Module</label>
          <input type="text" name="module" id="module" className="form-control"  onChange={handleChange} required/>
        </div>
        <div className="mb-3">
          <label htmlFor="lieuFormation"  className='form-label'>Lieu de Formation</label>
          <select name="lieuFormation" id="lieuFormation" className='form-control'  onChange={handleChange}>
          <option selected>Select ...</option>
          {
              etabDB.map((item)=>(
                <option value={item.etablissement_id}>{item.nom_etab}</option>
              ))
            }
          </select>
        </div>
        
        <div className="mb-3">
            <div className="row">
              <div className="col">
                <label htmlFor="dateDebut" className='form-label'>Date DÃ©but</label>
                <input type="date" name="dateDebut" id="dateDebut" className='form-control'  onChange={handleChange} required/>
              </div>
              <div className="col">
                <label htmlFor="dateFin" className='form-label'>Date Fin</label>
                <input type="date" name="dateFin" id="dateFin" className='form-control'  onChange={handleChange} required/>
              </div>
            </div>
        </div>
        
        <div className="mb-3">
          <label htmlFor="status" className='form-label'>Status</label>
          <select name="status" id="status" className='form-control' onChange={handleChange}>
          <option selected>Select ...</option>
            {
                statusDB.map((item)=>(
                  <option value={item.status_id}>{item.status}</option>
                ))
              }
            </select>
        </div>

        <div className="mb-3 my-5">
          <input type="submit" value="Enregistrer" className='btn btn-sm btn-success'/>
          <Link to={"/"} className='btn btn-sm btn-light mx-3'>Annuler</Link>
        </div>

      </form>

    </div>
  )
}

export default Formation
