import Header from './Header'
import { Link,useParams } from 'react-router-dom'
import { React,useEffect, useState } from 'react'
import axios from 'axios'


function UpdateParticipant() {
    
    const {id} = useParams();
    const [data,setData] = useState([])
    const [values,setValues] = useState({
        ppr:"",
        nom_pr:"",
        etablissement_id:"",
        formation_id:""

    })

    useEffect(()=>{
        const fetchData = async ()=>{
          try {
    
            const res = await axios.get("http://localhost:5000/participant/"+id)
            // console.log(res.data)
            setData(res.data)
            
          } catch (error) {
            console.log(error);
          }
        }
        
        fetchData();
      },[])


    const handlePPR = (e) =>{
        setValues((prev) =>({...prev,ppr: e.target.value}))
    }

  return (
    <div className='container'>
         <Header />
        <h3>Modifier</h3>
        <hr />
        <form  className='col-6'>
            <div className="mb-3">
                <label htmlFor="ppr" className='form-label'>PPR</label>
                {
                    data.map(item =>(
                        <input type="number" id='ppr' name='ppr' value={item.ppr} min={1111111} max={9999999}  onChange={handlePPR} className="form-control" placeholder='123567' required />
                    ))
                }
            </div>
            <div className="mb-3">
                <label htmlFor="nom_prof" className='form-label'>Nom Professeur </label>
                {
                    data.map(item=>(
                        <input type="text" id='nom_prof' name='nom_pr' value={item.nom_pr} onChange={handleChange} className="form-control" placeholder='Nom et prÃ©nom' required />
                    ))
                }
            </div>
            <div className="mb-3">
                <label htmlFor="nom_etab" className='form-label'>Nom Etablissement</label>
                <select id="nom_etab" name="etablissement_id" onChange={handleChange} className='form-control'>
                    {
                        data.map(item=>(
                            <option selected value={item.etablissement_id}>{item.nom_etab}</option>
                        ))
                    }
                </select>
            </div>

            <input type="submit" className='btn btn-success' value="Enregistrer" />
            <Link to={-1} className='btn btn-secondary mx-3'>Annuler</Link>
        </form>
    </div>
  )
}

export default UpdateParticipant
