import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import Header from '../components/Header'
import axios from 'axios'


function Show() {

  const [data,setData] = useState([]);
  const {id} = useParams();

  useEffect(()=>{
    const fetchData = async ()=>{
      try {

        const res = await axios.get("http://localhost:5000/show/"+id)
        // console.log(res.data)
        setData(res.data)
        
      } catch (error) {
        console.log(error);
      }
    }
    
    fetchData();
  },[])

  return (
    <div className='container'>
      <Header />

      <table class="table">
        
          {
            data.map(item , index =>(
              <tbody>
                <tr>
                  <th>Axe :</th>
                  <td key={index}>{item.nom_axe}</td>
                </tr>
                <tr>
                  <th>Etablissement :</th>
                  <td>{item.nom_etab}</td>
                </tr>
                <tr>
                  <th>Module :</th>
                  <td>{item.module}</td>
                </tr>
                <tr>
                  <th>Date DÃ©but :</th>
                  <td>{item.date_debut}</td>
                </tr>
                <tr>
                  <th>Date Fin :</th>
                  <td>{item.date_fin}</td>
                </tr>
                                
             </tbody>
            ))
          }

      </table>



    </div>
  )
}

export default Show
