import React, { useEffect, useState, useParams } from 'react'
import Header from '../components/Header'
import axios from 'axios'


function Show() {

  const [data,setData] = useState({});
  const {idFormation} = useParam();

  useEffect(()=>{
    const fetchData = async ()=>{
      try {

        const res = await axios.get("http://localhost:5000/formation/"+idFormation)
        // console.log(res.data)
        setData(res.data)
        
      } catch (error) {
        console.log(error);
      }
    }
    
    fetchData();
  },[])

  return (
    <div className='container'>
      <Header />

      <table class="table">
        
        <tbody>
          
            {
              data.map((item)=>(
                <tr>
                  <th>dd</th>
                  <td>Eleve</td>
                </tr>
              ))
            }
            
          
        </tbody>
      </table>



    </div>
  )
}

export default Show
