import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Header from './Header'

function Formation() {

  const [projetDB,setProjetDB] = useState([]);
  const [axeDB,setAxeDB] = useState([]);
  const [etabDB,setEtabDB] = useState([]);

  useEffect(()=>{

    axios.get("http://localhost:5000/projet")
    .then(res=>{
      setProjetDB(res.data);
    })
    .catch(err => console.log(err));

    axios.get("http://localhost:5000/axe")
    .then(res=>{
      setAxeDB(res.data);
    })
    .catch(err => console.log(err));

    axios.get("http://localhost:5000/etablissement")
    .then(res=>{
      setEtabDB(res.data);
    })
    .catch(err => console.log(err));

  },[])

  return (
    <div className='container'>
      <Header />
      <form >
        
        <div className="mb-3">
          <label htmlFor="Annee Scolaire" className="form-label">Annee Scolaire</label>
          <select name="" id="Annee Scolaire" className="form-control">
            <option value="2023/2024">2023/2024</option>
            <option value="2024/2025">2024/2025</option>
            <option value="2025/2026">2025/2026</option>
          </select>
        </div>
        <div className="mb-3">
          <div className="row">
            <div className="col">
            <label htmlFor="axe" className='form-label'>Axe</label>
              <select name="axe" id="axe" className="form-control">
            {
              axeDB.map((item)=>(
                <option value={item.nom_axe}>{item.nom_axe}</option>
              ))
            }
          </select>
          
            </div>
            <div className="col">
            <label htmlFor="projet"  className='form-label'>Projet</label>
          <select name="projet" id="projet" className="form-control">
            {
              projetDB.map((item)=>(
                <option value={item.nom_project}>{item.nom_projet}</option>
              ))
            }
          </select>
            </div>
          </div>
          
        </div>

        <div className="mb-3">
          <label htmlFor="module"  className='form-label'>Module</label>
          <input type="text" name="module" id="module" className="form-control" />
        </div>
        <div className="mb-3">
          <label htmlFor="lieuFormation"  className='form-label'>Lieu de Formation</label>
          <select name="lieuFormation" id="lieuFormation">
          {
              etabDB.map((item)=>(
                <option value={item.nom_etab}>{item.nom_etab}</option>
              ))
            }
          </select>
        </div>


      </form>

    </div>
  )
}

export default Formation
