import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import Header from './Header';

function AddProf() {
    // Get Formation ID
    const {id} = useParams();
    const [etab,setEtab] = useState([])
    const [values,setValues] = useState({
        "ppr":"",
        "nom_pr":"",
        "etablissement_id":"",
        "formation_id":""
    })

    useEffect(()=>{
        const fetchData = async ()=>{
          try {
    
            const res = await axios.get("http://localhost:5000/http://localhost:5000/etablissement")
            setEtab(res.data)
            
          } catch (error) {
            console.log(error);
          }
        }
        
        fetchData();
      },[])



    
  return (
    <div className='container'>
        <Header />
        <h3>Ajouter un nouveau</h3>
        <hr />
        <form className='col-6'>
            <div className="mb-3">
                <label htmlFor="ppr" className='form-label'>PPR</label>
                <input type="number" id='ppr' className="form-control" placeholder='0000000' required />
            </div>
            <div className="mb-3">
                <label htmlFor="nom_prof" className='form-label'>Nom Professeur </label>
                <input type="text" id='nom_prof' className="form-control" placeholder='Nom et prÃ©nom' required />
            </div>
            <div className="mb-3">
                <label htmlFor="nom_etab" className='form-label'>Nom Etablissement</label>
                <select name="" id="nom_etab" className='form-control'>
                    {
                        etab.map(item =>(
                            <option value={item.etablissement_id}>{item.nom_etab}</option>
                        ))
                    }
                </select>
            </div>

            <input type="submit" className='btn btn-success' value="Enregistrer" />
            <Link to={-1} className='btn btn-secondary mx-3'>Annuler</Link>
        </form>
    </div>
  )
}

export default AddProf
